void show_string(uint8_t *str,int16_t x,int16_t y,uint8_t csize,uint16_t fc, uint16_t bc,boolean mode)
{
    my_lcd.Set_Text_Mode(mode);
    my_lcd.Set_Text_Size(csize);
    my_lcd.Set_Text_colour(fc);
    my_lcd.Set_Text_Back_colour(bc);
    my_lcd.Print_String(str,x,y);
}

int idx( int spriteIdx, int Line, int Column, int Width )
{
  int value = spriteIdx * sizeof(uint16_t) * 1024;
  return ( (Line * Width + Column) + value );
}

int idx( int Line, int Column, int Width )
{
  return ( Line * Width + Column );
}

void Print_Sprite_Airplane( uint16_t X, uint16_t Y, uint16_t scale, uint8_t frame, uint16_t bgColor)
{
  int line, column;
  int spriteHeight = 32;
  int spriteWidth = 32;
  uint32_t color;
  uint16_t count=0;

  for(line = 0; line < spriteHeight; line++)
  {
    for(column = 0; column < spriteWidth; column++)
    {
 
      color = pgm_read_word_near( sprite1[frame] + count++);
      switch( color )
      {
        case 0x0000 : color = bgColor;    break; // fundo transparente?
        case 0xFFE0 : color = my_lcd.Color_To_565(255, 255, 0); break; 
        case 0xFF00 : color = BLACK; break; 
        //case 0XFF00 : color = my_lcd.Color_To_565(0, 0, 102); break;  
      }
      //color = (color == 0 ? BLUE : color );
      //Serial.print( count ); Serial.print(" ");
      my_lcd.Fill_Rect(X+column*scale, Y+line*scale, scale, scale, color);
    }
    //Serial.println(" ");
  }  
  
}

void Print_Sprite_Airplane( uint16_t X, uint16_t Y, uint16_t scale, uint8_t frame, uint16_t bgColor, bool cleanSprite)
{

  if( cleanSprite )
  {
    int line, column;
    int spriteHeight = 32;
    int spriteWidth = 32;
  
    for(line = 0; line < spriteHeight; line++)
    {
      for(column = 0; column < spriteWidth; column++)
      {
        my_lcd.Fill_Rect(X+column*scale, Y+line*scale, scale, scale, bgColor);
      }
    }  
  }
  else Print_Sprite_Airplane( X, Y, scale, frame, bgColor);
}


void setup() 
{
  Serial.begin(9600);
  my_lcd.Init_LCD();
  Serial.println(my_lcd.Read_ID(), HEX);
  my_lcd.Set_Rotation(1);
  //my_lcd.Fill_Screen( BLUE );

}

void loop() 
{

  my_lcd.Fill_Screen( BLUE );

  uint8_t scale = 3; 
  uint16_t iterations = my_lcd.Get_Width() / (32 * scale); // 32 = largura do sprite
  /*
  for(int k=0; k < iterations; k++)
  {
    uint16_t x = k * 32 * scale;
    uint16_t y = 20;
    Print_Sprite_Airplane(x, y, 1, 2);
    //delay(500);
  } */

  uint8_t frame=0;
  uint8_t k = 0;
  uint16_t x, Xant = 0;
  uint16_t y, Yant = 0;
  bool limpa = false;
  while(1)
  {
    if( limpa )
    {
      Print_Sprite_Airplane(Xant, Yant, scale, frame, BLUE, true);  
      limpa = false;
    }
    //uint16_t x = ( my_lcd.Get_Width()  - 32*scale ) / 2; // center
    uint16_t x = Xant = k * 32 * scale;
    uint16_t y = Yant = ( my_lcd.Get_Height() - 32*scale ) / 2;
    Print_Sprite_Airplane(x, y, scale, frame, BLUE, false);
    //frame = (frame >= 2 ? 0 : frame + 1 );
    frame++;
    if(frame >= 2)
    {
      frame = 0;
      k++;
      limpa = true;      
    }
    if( k >= iterations ) 
    {
      k = 0;
    }
    //Serial.print(x); Serial.print(" "); Serial.print(y); Serial.println("");
    delay(500);
  }

  
  //Print_Sprite_Airplane(150, 150, 2, 2);
  //Print_Sprite_Airplane(10, 10, 1, 0);

  
  delay(50000);

}
